SELECT B.USER_ID, B.NICKNAME,CONCAT(B.CITY,' ',B.STREET_ADDRESS1," ",B.STREET_ADDRESS2) AS '전체주소', CONCAT(SUBSTRING(B.TLNO FROM 1 FOR 3),'-',SUBSTRING(B.TLNO FROM 4 FOR 4),'-', SUBSTRING(B.TLNO FROM 8 FOR 4)) AS '전화번호' FROM (SELECT *, COUNT(*) AS WRITE_COUNT FROM USED_GOODS_BOARD GROUP BY WRITER_ID) A JOIN USED_GOODS_USER B ON A.WRITER_ID=B.USER_ID  WHERE A.WRITE_COUNT >2 ORDER BY B.USER_ID DESC;