SELECT B.CATEGORY,B.PRICE AS MAX_PRICE, B.PRODUCT_NAME FROM FOOD_PRODUCT B JOIN (SELECT CATEGORY, MAX(PRICE) AS MAX_PRICE FROM FOOD_PRODUCT WHERE CATEGORY="과자" || CATEGORY="국" || CATEGORY="김치" || CATEGORY="식용유" GROUP BY CATEGORY) A ON A.MAX_PRICE=B.PRICE AND A.CATEGORY=B.CATEGORY ORDER BY B.PRICE DESC;