SELECT A.ANIMAL_ID, A.ANIMAL_TYPE, A.NAME FROM ANIMAL_OUTS A WHERE SUBSTRING_INDEX((SELECT SEX_UPON_INTAKE FROM ANIMAL_INS B WHERE A.ANIMAL_ID=B.ANIMAL_ID),' ',1)="Intact" AND (SUBSTRING_INDEX(A.SEX_UPON_OUTCOME,' ',1)="Spayed" || SUBSTRING_INDEX(A.SEX_UPON_OUTCOME,' ',1)="Neutered") ORDER BY A.ANIMAL_ID ASC;